import React, { useState } from "react";
import axios from 'axios';

export const Login = (props) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [jwt, setJwt] = useState("");

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(username);
        console.log(password);
    }

    function sendLoginRequest() {
        const reqBody = {
            username: username,
            password: password,
        };

//         fetch("http://localhost:8080/api/auth/login", {
//             headers: {
//                 "Content-Type": "application/json",
//             },
//             method: "post",
//             body: JSON.stringify(reqBody),
//         })
        const body = JSON.stringify(reqBody);
        const headers = {
            'Content-Type': 'application/json',
        };
        axios.post('http://localhost:8080/api/auth/login',body, { headers })
            .then((response) => {
                if(response.status === 200)
                    return Promise.all([response.json(), response.headers]);
                else
                    return Promise.reject("Invalid Login Attempt!");
            })
            .then(([body, headers]) => {
                setJwt(headers.get("authorization"));
                props.setCurrentForm("home");
            }).catch((message) => {
                    alert(message);
            });
    }

    return (
        <div className="auth-form-container">
            <h2>Login</h2>
            <form className="login-form" onSubmit={() => sendLoginRequest()}>
                <label htmlFor="username">Username: </label>
                <input
                    value={username}
                    onChange={(event) => setUsername(event.target.value)}
                    type="username"
                    placeholder="Enter your username"
                    id="username"
                    name="username" />
                <label htmlFor="password">Password: </label>
                <input
                    value={password}
                    onChange={(event) => setPassword(event.target.value)}
                    type="password"
                    placeholder="Enter your password"
                    id="password"
                    name="password" />
                <button type="submit">Login</button>
            </form>
            <button className="link-button" onClick={() => props.onFormSwitch('register')}>Don't have an account? Register here.</button>
        </div>
    )
}
